sys_script_include.script:
var GraphQL = Class.create();
GraphQL.prototype = {
	initialize: function() {
	},

	/*
	 * setupMessage - Create a requestBody string based on a query ID name
	 *                and variables object
	 *
	 * @param requestBody - String
	 * @param myVars - Object
	 * @returns String
	 *
	 */
	setupMessage : function(queryID, myVars) {		

		// Get the actual query
		var qGr = new GlideRecord(Constants.QUERY_TABLE);
		qGr.addQuery('active', true);
		qGr.addQuery('id', queryID);
		qGr.query();

		if (!qGr.next()) {
			return '';
		}

		// Make it Meetup friendly
		var myQuery = qGr.getValue('query');
		myQuery = myQuery.replace(/"/g, "\\\"").replace(/[\n\r\t]/g, ' ').replace(/  +/g, ' ');

		// Now put the query and the variables into the body object
		var requestBody = JSON.stringify({
			"query"     : myQuery,
			"variables" : JSON.stringify(myVars)
		});

		return requestBody;

	},

	type: 'GraphQL'
};
