x_snc_meetup_com_query.query:
query ($urlname: String!, $itemsNum: Int!, $cursor:String) {
  groupByUrlname(urlname: $urlname) {
    id
    name
    urlname
    memberships(input: {first: $itemsNum, after: $cursor}) {
      count
      pageInfo {
        endCursor
        hasNextPage
      }
      edges {
        node {
          id
          name
          username
          email
          status
          isLeader
          memberPhoto {
            id
            baseUrl
          }
        }
      }
    }
  }
}
