x_snc_meetup_com_query.query:
query ($urlname: String!, $itemsNum: Int!, $cursor: String) {
  groupByUrlname(urlname: $urlname) {
    id
    name
    link
    pastEvents(
		input: {
			first: $itemsNum,
			after: $cursor
		}
	) {
      count
      pageInfo {
        endCursor
        hasNextPage
      }
      edges {
        cursor
        node {
          id
          title
          eventUrl
          dateTime
        }
      }
    }
  }
}
