x_snc_meetup_com_query.query:
query ($urlname: String!) {
  proNetworkByUrlname(urlname: $urlname) {
    groupsSearch(input: {
		first: 1000
	}) {
      edges {
        node {
          id
          name
          upcomingEvents(input: {
            first: 1000
          }) {
            edges {
              node {
                id
                title
                dateTime
                tickets(
                  input: {
                  first: 1000,
                  status: [
                      YES,
                      NO,
                      WAITLIST,
                      MAYBE,
                      ATTENDED,
                      NO_SHOW, 
                      EXCUSED_ABSENCE, 
                      YES_PENDING_PAYMENT
                  ]
                }) {
                  edges {
                    node {
                      id
                      status
                      user {
                        id
                        name
                        email
                      }
                    }
                  }
                }
              }
            }
          }
        }
      }
    }
  }
}
