x_snc_meetup_com_query.query:
query ($urlname: String!, $itemsNum:Int!) {
  groupByUrlname(urlname: $urlname) {
    id
    name
    link
    eventSearch(
      filter:{
        query: ""
      },
      input: {
        first: $itemsNum
      }

    ) {
      count
      pageInfo {
        endCursor
        hasNextPage
      }
      edges {
        cursor
        node {
          id
          title
          eventUrl
          dateTime
        }
      }
    }
  }
}
