x_snc_meetup_com_query.query:
query ($urlname: String!, $itemsNum: Int!, $cursor: String) {
  proNetworkByUrlname(urlname: $urlname) {
    id
    name
    status
    link
    groupsSearch(input: {first: $itemsNum, after: $cursor}) {
      count
      pageInfo {
        endCursor
        hasNextPage
      }
      edges {
        node {
          id
          name
          urlname
		  status
          memberships {
            count
          }
        }
      }
    }
  }
}
