x_snc_meetup_com_query.query:
query ($urlname: String!, $itemsNum: Int!, $cursor: String) {
  proNetworkByUrlname(urlname: $urlname) {
    eventsSearch(input: {first: $itemsNum, after: $cursor}) {
      count
      pageInfo {
        endCursor
        hasNextPage
      }
      edges {
        cursor
        node {
          id
          title
          description
          status
          dateTime
          eventUrl
          group {
            id
          }
        }
      }
    }
  }
}
