x_snc_meetup_com_query.query:
query ($eventId: ID!, $itemsNum: Int!, $cursor: String) {
  event(id: $eventId) {
    title
    id
    eventUrl
    description
    dateTime
    duration
    host {
      id
      name
    }
    images {
      id
      baseUrl
      preview
    }
    group {
      id
      name
      urlname
    }
    tickets(input: {
		first: $itemsNum,
		after: $cursor
	}) {
      pageInfo {
        hasNextPage
        endCursor
      }
      yesCount
      noCount
      waitlistCount
      attendedCount
      absentCount
      noShowCount
      edges {
        node {
          id
          status
          quantity
          guestsCount
          user {
            name
            email
          }
        }
      }
    }
  }
}
