x_snc_meetup_com_query.query:
query ($eventId: ID!, $itemsNum: Int!, $cursor: String) {
  event(id: $eventId) {
    title
    id
    eventUrl
    tickets(input: {
		first: $itemsNum,
		after: $cursor
		status : [
			YES,
			NO,
			WAITLIST,
			ATTENDED,
			NO_SHOW,
			EXCUSED_ABSENCE,
			HAVENT,
			YES_PENDING_PAYMENT
		]
	}) {
      pageInfo {
        hasNextPage
        endCursor
      }
      yesCount
      noCount
      waitlistCount
      attendedCount
      absentCount
      noShowCount
      edges {
        node {
          id
          status
          quantity
          guestsCount
          user {
            id
            name
			username
            email
            memberPhotoUrl
			memberUrl
            isOrganizer
            isLeader
          }
        }
      }
    }
  }
}
